         , dataset = map(measure, function(var) df_input %>%
                           filter(!is.na(study_cohort) & pod_ons_new == "Home") %>% 
                           select(cohort, imd_quintile, all_of(var)) %>%
                           rename(value = all_of(var)) %>%
                           mutate(characteristic = "imd_quintile"
